import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';

const URI = 'http://localhost:8000/usuarios';

const CompShowUsuarios = () => {
    const [usuarios, setUsuarios] = useState([]);
    const [showModal, setShowModal] = useState(false); // Estado para el modal
    const [message, setMessage] = useState(''); // Mensaje del modal

    useEffect(() => {
        getUsuarios();
    }, []);

    const getUsuarios = async () => {
        try {
            const res = await axios.get(URI, {
                headers: {
                    "Authorization": `Bearer ${localStorage.getItem("token")}`,
                },
            });
            setUsuarios(res.data);
        } catch (error) {
            console.error("Error al obtener los usuarios:", error);
        }
    };

    const deleteUsuario = async (id_usuario) => {
        const token = localStorage.getItem('token');
        try {
            await axios.delete(`${URI}/${id_usuario}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            setMessage('Usuario eliminado con éxito');
            setShowModal(true);
            getUsuarios();
        } catch (error) {
            console.error('Error al eliminar el usuario:', error);
            setMessage('Hubo un error al eliminar el usuario');
            setShowModal(true);
        }
    };

    const handleClose = () => {
        setShowModal(false);
    };

    const styles = {
        container: {
            margin: '20px',
        },
        button: {
            margin: '5px',
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            cursor: 'pointer',
        },
        createButton: {
            backgroundColor: '#4CAF50',
            color: 'white',
        },
        editButton: {
            backgroundColor: '#2196F3',
            color: 'white',
        },
        deleteButton: {
            backgroundColor: '#f44336',
            color: 'white',
        },
        table: {
            width: '100%',
            borderCollapse: 'collapse',
        },
        th: {
            backgroundColor: '#f2f2f2',
            border: '1px solid #ddd',
            padding: '8px',
        },
        td: {
            border: '1px solid #ddd',
            padding: '8px',
            textAlign: 'center',
        },
        trHover: {
            backgroundColor: '#f1f1f1',
        },
    };

    return (
        <div style={styles.container}>
            <Link 
                to="/create-usuario" 
                style={{ ...styles.button, ...styles.createButton }}
            >
                Crear Usuario
            </Link>
            <div className="table-responsive">
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th style={styles.th}>Nombre</th>
                            <th style={styles.th}>Email</th>
                            <th style={styles.th}>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {usuarios.map((usuario) => (
                            <tr key={usuario.id_usuario} style={styles.trHover}>
                                <td style={styles.td}>{usuario.nombre}</td>
                                <td style={styles.td}>{usuario.email}</td>
                                <td style={styles.td}>
                                    <div>
                                        <Link 
                                            to={`/edit-usuario/${usuario.id_usuario}`} 
                                            style={{ ...styles.button, ...styles.editButton }}
                                        >
                                            Editar
                                        </Link>
                                        <button 
                                            onClick={() => deleteUsuario(usuario.id_usuario)} 
                                            style={{ ...styles.button, ...styles.deleteButton }}
                                        >
                                            Eliminar
                                        </button>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <Modal show={showModal} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{message.startsWith('Hubo') ? '¡Error!' : '¡Éxito!'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>{message}</Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        Aceptar
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default CompShowUsuarios;
