import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./ResultadoBus.css"; // Asegúrate de que este archivo tenga los estilos necesarios

const ResultadoBus = () => {
  const { termino } = useParams(); // Obtiene el término de búsqueda de la URL
  const [libros, setLibros] = useState([]); // Estado para almacenar los resultados
  const [error, setError] = useState(""); // Estado para almacenar el error
  const [isLoading, setIsLoading] = useState(true); // Estado para manejar la carga
  const [isRegistered, setIsRegistered] = useState(false); // Estado para simular el registro del usuario
  const [showActions, setShowActions] = useState(false); // Estado para mostrar la columna de acción
  const navigate = useNavigate(); // Hook de navegación para redirigir

  useEffect(() => {
    const fetchLibros = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/libros/buscar/${termino}`);
        setLibros(response.data); // Almacena los libros obtenidos
      } catch (err) {
        setError("Error al cargar los libros."); // En caso de error, muestra un mensaje
      } finally {
        setIsLoading(false); // Termina la carga
      }
    };

    fetchLibros(); // Llama a la función para obtener los libros cuando se monta el componente
  }, [termino]);

  const handleReservar = (id_libro) => {
    if (!isRegistered) {
      // Si no está registrado, redirige al login
      navigate("/login"); // Asegúrate de que esta ruta sea correcta
    } else {
      // Si está registrado, muestra la columna de acción
      setShowActions(true);
      navigate(`/reservar/${id_libro}`);
    }
  };

  const handlePedirPrestado = (id_libro) => {
    navigate(`/formulario-prestamo/${id_libro}`);
  };

  if (isLoading) {
    return <div className="container mt-4">Cargando resultados...</div>; // Muestra un mensaje de carga
  }

  return (
    <div className="resultado-bus-container">
      <h2 className="resultado-title">
        Resultados de la búsqueda {termino ? `para "${termino}"` : ""}
      </h2>

      {error && <div className="alert alert-danger">{error}</div>}

      {libros.length > 0 ? (
        <table className="resultado-table">
          <thead>
            <tr>
              <th>Autor</th>
              <th>Título</th>
              {showActions && <th>Acción</th>} {/* Solo muestra la columna "Acción" si showActions es true */}
            </tr>
          </thead>
          <tbody>
            {libros.map((libro) => (
              <tr key={libro.id_libro}>
                <td>{libro.autor}</td>
                <td>{libro.titulo}</td>
                {showActions && (
                  <td>
                    <button
                      className="btn reservar-btn"
                      onClick={() => handleReservar(libro.id_libro)}
                    >
                      Reservar
                    </button>
                    <button
                      className="btn pedir-prestado-btn"
                      onClick={() => handlePedirPrestado(libro.id_libro)}
                    >
                      Pedir prestado
                    </button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-center text-muted">
          {termino ? `No se encontraron libros para "${termino}"` : "Realiza una búsqueda para ver resultados."}
        </p>
      )}

      {/* Botón de "Reservar" para mostrar la columna de acciones */}
      {!showActions && (
        <button onClick={() => handleReservar()} style={{ marginTop: "20px", padding: "8px 16px" }}>
          Reservar
        </button>
      )}
    </div>
  );
};

export default ResultadoBus;
