import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RestablecerContrasena = () => {
  const { token } = useParams();
  const navigate = useNavigate();
  const [nuevaContrasena, setNuevaContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isTokenValid, setIsTokenValid] = useState(false);

  useEffect(() => {
    // Verificar el token al cargar el componente
    axios.get(`http://localhost:8000/usuarios/restablecer/${token}`)
      .then(response => {
        if (response.data.isValid) {
          setIsTokenValid(true); // Si el token es válido, habilitar el formulario
        } else {
          setError('El token no es válido o ha expirado');
        }
      })
      .catch(() => {
        setError('El token no es válido o ha expirado');
      });
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await axios.post(`http://localhost:8000/usuarios/actualizar/${token}`, { nuevaContrasena });
      setMensaje(response.data.message);
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error al actualizar la contraseña');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="restablecer-container">
      <h2>Restablecer Contraseña</h2>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {mensaje && <p style={{ color: 'green' }}>{mensaje}</p>}

      {isTokenValid && !mensaje && !error && (
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="nuevaContrasena">Nueva Contraseña</label>
            <input
              type="password"
              id="nuevaContrasena"
              value={nuevaContrasena}
              onChange={(e) => setNuevaContrasena(e.target.value)}
              required
            />
          </div>
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Actualizando...' : 'Actualizar Contraseña'}
          </button>
        </form>
      )}
    </div>
  );
};

export default RestablecerContrasena;
